import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './reception.css'

const AllPatients = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredData, setFilteredData] = useState([]);
    // const [allPatients, setAllPatients] = useState([])

    // useEffect(() => {
    //     const apiUrl = 'http://localhost:3000/allPatients';

    //     axios.get(apiUrl)
    //         .then(response => {
    //             setReceptionQueue(response.data);
    //         })
    //         .catch(error => {
    //             console.error('Error fetching reception queue data:', error);
    //         });
    // }, []); 

    // Your sample JSON data

    // Function to handle search


    const sampleData = [
        {
            "uniqueNumber": "A2",
            "name": "אברהם שלום",
            "roomId": "12fhi23",
            "turnId": 2
        },
        {
            "uniqueNumber": "B1",
            "name": "מרים כהן",
            "roomId": "12fhi23",
            "turnId": 1
        },
        {
            "uniqueNumber": "C3",
            "name": "דניאל יוסף",
            "roomId": "12fhi23",
            "turnId": 3
        },
        {
            "uniqueNumber": "C3",
            "name": "דניאל יוסף",
            "roomId": "12fhi23",
            "turnId": 4
        },
        {
            "uniqueNumber": "C3",
            "name": "דניאל יוסף",
            "roomId": "12fhi23",
            "turnId": 5
        },
        {
            "uniqueNumber": "C3",
            "name": "דניאל יוסף",
            "roomId": "12fhi23",
            "turnId": 6
        },
        {
            "uniqueNumber": "C3",
            "name": "דניאל יוסף",
            "roomId": "12fhi23",
            "turnId": 7
        }, {
            "uniqueNumber": "A2",
            "name": "אברהם שלום",
            "roomId": "12fhi23",
            "turnId": 8
        },
        {
            "uniqueNumber": "B1",
            "name": "מרים כהן",
            "roomId": "12fhi23",
            "turnId": 9
        },
        {
            "uniqueNumber": "C3",
            "name": "דניאל יוסף",
            "roomId": "12fhi23",
            "turnId": 10
        }
    ];
    // Function to handle search
    const handleSearch = () => {
        // Filter data based on the search term
        const result = sampleData.filter(
            (item) =>
                item.name.includes(searchTerm) || item.uniqueNumber.includes(searchTerm)
        );

        setFilteredData(result);
    };

    return (
        <div>
            <h2>All Patients</h2>
            <div className='searchContainer'>
                <input
                    type="text"
                    placeholder="Search by name or number"
                    value={searchTerm}
                    onChange={(e) => {
                        setSearchTerm(e.target.value);
                        handleSearch();
                    }}
                />
            </div>
            <div className='patientsTable'>
                <table>
                    <thead>
                        <tr>
                            <th>שם מטופל</th>
                            <th>מספר</th>
                        </tr>
                    </thead>
                    <tbody>
                        {(filteredData.length > 0 ? filteredData : sampleData).map((item) => (
                            <tr key={item.turnId}>
                                <td>{item.name} </td>
                                <td>{item.uniqueNumber}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default AllPatients;
