import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BarChart } from '@mui/x-charts/BarChart';
import './chart.css';

export default function ChartsOverviewDemo() {
    const [chartData, setChartData] = useState([]);

    useEffect(() => {
        const fetchReportData = async () => {
            try {
                const today = new Date().toISOString().split('T')[0];
                const apiUrl = `http://localhost:3001/reportTable?date=${today}`;

                const response = await axios.get(apiUrl);
                const data = response.data.data;

                // Check if data is an array
                if (Array.isArray(data) && data.length > 0) {
                    const formattedChartData = Object.keys(data[0])
                        .filter(key => key.startsWith('clinic'))
                        .map(clinicKey => data[0][clinicKey]);

                    setChartData(formattedChartData);
                } else {
                    console.error('Invalid data format:', data);
                }

            } catch (error) {
                console.error('Error fetching report data:', error);
            }
        };

        fetchReportData();
    }, []);

    return (
        <div className='chartCont'>
            <BarChart
                series={[{ data: chartData }]}
                height={290}
                xAxis={[{ data: ['מאוחדת', 'מכבי', 'כללית', 'לאומית', 'אחר'], scaleType: 'band' }]}
                margin={{ top: 10, bottom: 30, left: 40, right: 10 }}
                containerProps={{ width: 600 }}
            />
        </div>
    );
}
