import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './messages.css';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';

const MessageInput = ({ onSendMessage }) => {
  const [message, setMessage] = useState('');

  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };

  const handleSendMessage = () => {
    if (message.trim() !== '') {
      onSendMessage(message);
      setMessage('');

      // Send the message to the server using Axios
      axios.post('http://localhost:3001/messageTable', { message })
        .then(response => {
          console.log('Message sent successfully:', response.data);
          // Handle any additional logic if needed
        })
        .catch(error => {
          console.error('Error sending message:', error);
          // Handle errors if needed
        });
    }
  };

  return (
    <div className='createText'>
      <textarea
        placeholder="הכנס טקסט"
        value={message}
        onChange={handleMessageChange}
      />
      <button className='sendText' onClick={handleSendMessage}>שלח</button>
    </div>
  );
};

const MessageScreen = ({ messages, onDeleteMessage }) => {
  return (
    <div className='messagesScreenCont'>
      <div className='messagesScreen'>
        <h2>הודעות</h2>
        {messages.map((message, index) => (
          <div className='message' key={index}>
            <div className='messagesTools'>
              <span className='deleteMessage' onClick={() => onDeleteMessage(index)}>
                <DeleteIcon />
              </span>
              <span className='editMessage'><EditIcon /></span>
            </div>
            {message}
          </div>
        ))}
      </div>
    </div>
  );
};

const Messages = () => {
  const [messages, setMessages] = useState([]);

  const handleSendMessage = (newMessage) => {
    setMessages([...messages, newMessage]);
  };

  const handleDeleteMessage = (index) => {
    const messageId = index;

    axios.delete(`http://localhost:3001/messageTable/${messageId}`)
      .then(response => {
        console.log('Message deleted successfully:', response.data);
        const updatedMessages = [...messages];
        updatedMessages.splice(index, 1);
        setMessages(updatedMessages);
      })
      .catch(error => {
        console.error('Error deleting message:', error);
      });
  };

  useEffect(() => {
    axios.get('http://localhost:3001/messageTable')
      .then(response => {
        setMessages(response.data.data);
      })
      .catch(error => {
        console.error('Error fetching messages:', error);
      });
  }, []);

  return (
    <div className='messagesCont'>
      <MessageInput onSendMessage={handleSendMessage} />
      <MessageScreen messages={messages} onDeleteMessage={handleDeleteMessage} />
    </div>
  );
};

export default Messages;
