import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './reception.css'
import AllPatients from './allPatients'
const ReceptionQueue = () => {
    const [selectedPatient, setSelectedPatient] = useState(null);
    const [selectedRoom, setSelectedRoom] = useState(""); // Add this line
    const [rooms, setRooms] = useState([
        "קבלה",
        "חדר טיפולים",
        "חדר טריאג",
        "חדר רופא",
        "חדר אקג"
    ]);

    const handleMovePatient = (selectedRoom) => {
        // Implement the logic to move the selected patient to the head of the queue
        console.log(`Move patient ${selectedPatient} to ${selectedRoom}`);
    };

    // const [receptionQueue, setReceptionQueue] = useState([]);

    // useEffect(() => {
    //     const apiUrl = 'http://localhost:3000/receptionQueue';

    //     axios.get(apiUrl)
    //         .then(response => {
    //             setReceptionQueue(response.data);
    //         })
    //         .catch(error => {
    //             console.error('Error fetching reception queue data:', error);
    //         });
    // }, []); 

    // Your sample JSON data
    const sampleQueues = [
        {
            "room": "קבלה"
        },
        {
            "room": "חדר טיפולים"
        }, {
            "room": "חדר טריאג"
        }, {
            "room": "חדר רופא"
        },
        {
            "room": "חדר אקג"
        }]
    const sampleData = [
        {
            "uniqueNumber": "A2",
            "name": "אברהם שלום",
            "roomId": "12fhi23",
            "turnId": 2
        },
        {
            "uniqueNumber": "B1",
            "name": "מרים כהן",
            "roomId": "12fhi23",
            "turnId": 1
        },
        {
            "uniqueNumber": "C3",
            "name": "דניאל יוסף",
            "roomId": "12fhi23",
            "turnId": 3
        },
        {
            "uniqueNumber": "C3",
            "name": "דניאל יוסף",
            "roomId": "12fhi23",
            "turnId": 4
        },
        {
            "uniqueNumber": "C3",
            "name": "דניאל יוסף",
            "roomId": "12fhi23",
            "turnId": 5
        },
        {
            "uniqueNumber": "C3",
            "name": "דניאל יוסף",
            "roomId": "12fhi23",
            "turnId": 6
        },
        {
            "uniqueNumber": "C3",
            "name": "דניאל יוסף",
            "roomId": "12fhi23",
            "turnId": 7
        }, {
            "uniqueNumber": "A2",
            "name": "אברהם שלום",
            "roomId": "12fhi23",
            "turnId": 8
        },
        {
            "uniqueNumber": "B1",
            "name": "מרים כהן",
            "roomId": "12fhi23",
            "turnId": 9
        },
        {
            "uniqueNumber": "C3",
            "name": "דניאל יוסף",
            "roomId": "12fhi23",
            "turnId": 10
        }
    ];
    return (
        <div>
            <h2>Reception Queue</h2>
            <div className='patientsTable'>
                <div className='scrollList'>
                    <table>
                        <thead>
                            <tr >
                                <th> שם מטופל</th>
                                <th>מספר</th>
                            </tr>
                        </thead>
                        <tbody>
                            {sampleData.map(item => (
                                <tr key={item.turnId}>
                                    <td>{item.name} </td>
                                    <td>{item.uniqueNumber}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

            </div>
            <AllPatients onPatientSelect={setSelectedPatient} />

            <div className='controllBoard'>
                <div>Current ID of the selected patient: {selectedPatient}</div>
                <div>Turn of the patient: {/* Implement logic to get turn based on selectedPatient */}</div>
                <select>
                    {rooms.map((room) => (
                        <option key={room}>{room}</option>
                    ))}
                </select>
                <button onClick={() => handleMovePatient(selectedRoom)}>
                    העבר לראש התור
                </button>
            </div>
        </div>
    );
};

export default ReceptionQueue;
