import React, { useState } from 'react';
import './print.css'

const PrintComponent = () => {
  const [isPrinting, setIsPrinting] = useState(false);

  const handlePrint = () => {
    setIsPrinting(true);
    window.print();
    setIsPrinting(false);
  };

  return (
    <div>
      {isPrinting ? (
        <div>
          <h1>מדפיס...</h1>
          <p>אנא המתן עד שההדפסה תסתיים.</p>
        </div>
      ) : (
        <button onClick={handlePrint}>הדפס</button>
      )}
    </div>
  );
};

export default PrintComponent;
