import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './reception.css'
import SearchIcon from '@mui/icons-material/Search';

const AllPatients = ({ onPatientSelect }) => {
  const [searchText, setSearchText] = React.useState("");
  const [selectedPatient, setSelectedPatient] = useState(null);

  // const [allPatients, setAllPatients] = useState([])

  // useEffect(() => {
  //     const apiUrl = 'http://localhost:3000/allPatients';

  //     axios.get(apiUrl)
  //         .then(response => {
  //             setReceptionQueue(response.data);
  //         })
  //         .catch(error => {
  //             console.error('Error fetching reception queue data:', error);
  //         });
  // }, []); 

  // Your sample JSON data

  // Function to handle search


  const sampleData = [
    {
      "uniqueNumber": "A2",
      "name": "אברהם שלום",
      "roomId": "12fhi23",
      "turnId": 2
    },
    {
      "uniqueNumber": "B1",
      "name": "מרים כהן",
      "roomId": "12fhi23",
      "turnId": 1
    },
    {
      "uniqueNumber": "C3",
      "name": "דניאל יוסף",
      "roomId": "12fhi23",
      "turnId": 3
    }, {
      "uniqueNumber": "U9",
      "name": " רחל לוי",
      "roomId": "12fhi23",
      "turnId": 7
    },
    {
      "uniqueNumber": "S3",
      "name": "שושנה אריה ",
      "roomId": "12fhi23",
      "turnId": 4
    },
    {
      "uniqueNumber": "G6",
      "name": "משה בן שלמה ",
      "roomId": "12fhi23",
      "turnId": 5
    },
    {
      "uniqueNumber": "k3",
      "name": "אברהם שלום",
      "roomId": "12fhi23",
      "turnId": 2
    },
    {
      "uniqueNumber": "d2",
      "name": "מרים כהן",
      "roomId": "12fhi23",
      "turnId": 1
    },
    {
      "uniqueNumber": "h8",
      "name": "דניאל יוסף",
      "roomId": "12fhi23",
      "turnId": 3
    }, {
      "uniqueNumber": "k0",
      "name": " רחל לוי",
      "roomId": "12fhi23",
      "turnId": 7
    },
    {
      "uniqueNumber": "j8",
      "name": "שושנה אריה ",
      "roomId": "12fhi23",
      "turnId": 4
    },
    {
      "uniqueNumber": "t7",
      "name": "משה בן שלמה ",
      "roomId": "12fhi23",
      "turnId": 5
    },

  ];
  const handlePatientSelect = (uniqueNumber) => {
    setSelectedPatient(uniqueNumber);
    // Notify the parent component about the selected patient
    onPatientSelect(uniqueNumber);
  };

  const filteredPatients = sampleData.filter(
    ({ name, uniqueNumber }) =>
      name.toLowerCase().includes(searchText.toLowerCase()) ||
      uniqueNumber.toLowerCase().includes(searchText.toLowerCase())
  );

  return (
    <div>
      <div className='patientSearch'>
        <div className='searchDiv'>
          <SearchIcon />
          <input
            className='selectPatient'
            placeholder='חיפוש מטופל...'
            type="text"
            title='חיפוש מטופל'

            value={searchText}
            onChange={({ target }) => setSearchText(target.value)}
          />
        </div>
        <div className='scrollList'>
          {filteredPatients.map(({ name, uniqueNumber }) => (
            <div className='allPatients' key={uniqueNumber}>
              <input
                className='patientDet'
                name='selectPatient'
                type="radio"
                value={uniqueNumber}
                checked={selectedPatient === uniqueNumber}
                onChange={() => handlePatientSelect(uniqueNumber)}
              />
              <span className='patientDet'> {name}</span>
              <span className='patientDet'>{uniqueNumber}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AllPatients;
