import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function SelectRoom() {
    const [rooms, setRooms] = useState([]);
    const navigate = useNavigate();

    const handleClick = (roomRoute) => {
        if (roomRoute === 'קבלה') {
            navigate('/reception');
        }

        if (roomRoute === 'חדר רופא');
        {
            navigate('/doctor');

        }
        if (roomRoute === 'חדר אקג');
        {
            navigate('/ecg');

        }
        if (roomRoute === 'חדר טיפולים');
        {
            navigate('/treatment');

        }
        if (roomRoute === 'חדר טריאג');
        {
            navigate('/triage');

        }

    };

    useEffect(() => {
        const apiUrl = 'http://localhost:3001/roomsTable';

        axios
            .get(apiUrl)
            .then((response) => {
                setRooms(response.data.data);
                console.log(response.data.data)
            })
            .catch((error) => {
                console.error('Error fetching rooms data:', error);
            });
    }, []);

    return (
        <>
            <div>
                <h1>vhhsdhqd</h1>
                {rooms.map((room) => (
                    <div key={room._id} onClick={() => handleClick(`${room.room}`)}>
                        {room.room}
                    </div>
                ))}
            </div>
        </>
    );
}
