import React, { useState } from 'react';
import './messages.css'

const MessageInput = ({ onSendMessage }) => {
  const [message, setMessage] = useState('');

  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };

  const handleSendMessage = () => {
    if (message.trim() !== '') {
      onSendMessage(message);
      setMessage('');
    }
  };

  return (
    <div className='createText'>
      <textarea 
        placeholder=" הכנס טקסט"
        value={message}
        onChange={handleMessageChange}
      />
      <button className='sendText' onClick={handleSendMessage}>שלח</button>
    </div>
  );
};

const MessageScreen = ({ messages }) => {
  return (
    <div>
      <h2>Message Screen</h2>
      <div className='messagesScreen'>
        {messages.map((message, index) => (
          <div className='message' key={index}>{message}</div>
        ))}
      </div>
    </div>
  );
};

const Messages = () => {
  const [messages, setMessages] = useState([]);

  const handleSendMessage = (newMessage) => {
    setMessages([...messages, newMessage]);
  };

  return (
    <div>
      <MessageInput onSendMessage={handleSendMessage} />
      <MessageScreen messages={messages} />
    </div>
  );
};

export default Messages;
